import React from 'react';
import Content from './Database.json';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import './Navigator.css';

const Navigator = () => {
    console.log("Content", Content);
    const [formData, setFormData] = React.useState(null);
    const [icon, setIcon] = React.useState(null);
    const renderFormData = (data) => {
        setFormData(data.tab);
    }
    React.useEffect(()=>{
        setFormData(Content.tabs[0].tab)
    },[])
    console.log("formData", formData);
    return(
        <div id="app" style={{textAlign: "center"}}>
             <h1>The Navigator</h1>
             <div className='Main' style={{display: "flex", margin:"10%"}}>
                <div className='Tabs'>
                    {Content.tabs.map((tab,key)=>(
                        <div key={key} className='tabList'>
                            <FontAwesomeIcon
                                icon="check-square"
                                size="lg" 
                                onClick={()=>renderFormData(tab)}
                                className="icons"
                            />
                            <hr className='line'></hr>
                            <div className='arrow_box'></div>
                        </div>
                    ))}
                </div>
                <div className='mainContent'>
                    <h2>{formData?.name}</h2>
                    <form className='formContent'>
                        <p className='description'>{formData?.description}</p><input className="textfield" type="text" name="firstSection" />
                    </form>
                </div>
             </div>
             <i className="fa fa-circle custom"></i>
        </div>
       
    )
}

export default Navigator;
