import React from 'react';
import Content from './Database.json';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import './Navigator.css';

const Navigator = () => {
    console.log("Content", Content);
    const [formData, setFormData] = React.useState(null);
    const renderFormData = (data) => {
        setFormData(data.tab);
    }
    React.useEffect(()=>{
        Content.tabs.forEach((tab, i) => {
            setFormData(tab);
            console.log("tab", tab[0])
        })
    })
    console.log("formData", formData);
    return(
        <div id="app" style={{textAlign: "center"}}>
             <h1>The Navigator</h1>
             <div className='Main' style={{display: "flex", margin:"10%"}}>
                <div className='Tabs'>
                    {Content.tabs.map((tab,key)=>(
                        <div key={key} className='tabList' onClick={()=>renderFormData(tab)}>
                            <FontAwesomeIcon icon="check-square" size="lg" />
                            <hr className='line'></hr>
                        </div>
                    ))}
                </div>
                <div className='mainContent'>
                    <form>
                    <p>{formData?.description}</p>
                    </form>
                </div>
             </div>
        </div>
       
    )
}

export default Navigator;
