import React from 'react';
import Content from './Database.json';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import './Navigator.css';

const Navigator = () => {
    console.log("Content", Content);
    const [formData, setFormData] = React.useState(null);
    const [icon, setIcon] = React.useState(null);
    const renderFormData = (data) => {
        setFormData(data.tab);
    }
    React.useEffect(()=>{
        setFormData(Content.tabs[0].tab);
        const initialTab = Content.tabs[0].tab;
        const iconType = getInitialRenderTab('Unavailable')
        setIcon(iconType);
    },[]);

    const getInitialRenderTab = (status) => {
        switch(status) {
            case 'Available': {
                return "fa-solid fa-circle-dot"
            }
            case 'Completed': {
                return "fa-solid fa-circle-check"
            }
            case 'Invalid': {
                return "fa-solid fa-circle-exclamation"
            }
            case 'Unavailable': {
                return "fa-solid fa-circle Unavailable"
            }
            default: {
                return;
            }
        }
    }
    console.log("formData", formData);
    return(
        <div id="app" style={{textAlign: "center"}}>
             <h1>The Navigator</h1>
             <div className='Main' style={{display: "flex", margin:"10%"}}>
                <div className='Tabs'>
                    {Content.tabs.map((item,key)=>(
                        <div key={key} className='tabList'>
                            {/* <i className={`${icon} dynamicIcon icons}`}
                                onClick={()=>renderFormData(item)}
                                style={{color: "#d2ddef"}}
                                id={item.tab.name}
                            /> */}
                            <FontAwesomeIcon icon={icon} className="dynamicIcon"
                            onClick={()=>renderFormData(item)}/> 
                            <hr className='line '></hr>
                            <div className='arrow_box'></div>
                        </div>
                    ))}
                </div>
                <div className='mainContent'>
                    <h2>{formData?.name}</h2>
                    <form className='formContent'>
                        <p className='description'>{formData?.description}</p><input className="textfield" type="text" name="firstSection" />
                    </form>
                </div>
             </div>
             <i className="fa fa-circle-exclamation icons" style={{color: "red"}}></i>
             <i class="fa-regular fa-circle-dot"></i>
             <i class="fa-duotone fa-circle-dot"></i>

             <FontAwesomeIcon icon="fa-solid fa-circle-dot" /> 
        </div>
       
    )
}

export default Navigator;
